// console.log(restof)



const Hello=(prop)=>{
  const {name , age}=prop
  return(
    <div>
    <p>
      Hello {name} you are {age}
    </p>
  </div>
  )
}



const R=(p)=>{
  const {r}=p
  return(
    <div> {r} </div>
  )
}

let rr = 123

const Application=()=>{
  const name='hgh'
  const age=34
  return(
    <div>
      <Hello name='hgh2' age={35} />
      <Hello name={name} age={age}/>
    </div>
  )
}

